import QtQuick
import QtQuick.Layouts
import QtQuick.Controls
import Quickshell
import Quickshell.Services.SystemTray

RowLayout {
    // The window the bar lives in; required for showing platform menus
    required property var window
    spacing: 8

    Repeater {
        model: SystemTray.items

        Item {
            required property SystemTrayItem modelData

            implicitWidth: 20
            implicitHeight: 20

            Image {
                anchors.fill: parent
                source: modelData.icon
                fillMode: Image.PreserveAspectFit
                smooth: true
            }

            MouseArea {
                id: mouseArea
                anchors.fill: parent
                acceptedButtons: Qt.LeftButton | Qt.RightButton | Qt.MiddleButton
                hoverEnabled: true

                onClicked: function(mouse) {
                    if (mouse.button === Qt.LeftButton) {
                        modelData.activate();
                    } else if (mouse.button === Qt.RightButton) {
                        if (modelData.menu && window) {
                            // Map local coordinates into window coordinates
                            var pos = mouseArea.mapToItem(window.contentItem, 0, mouseArea.height);
                            modelData.display(window, pos.x, pos.y);
                        }
                    } else if (mouse.button === Qt.MiddleButton) {
                        modelData.secondaryActivate();
                    }
                }

                onWheel: function(wheel) {
                    modelData.scroll(wheel.angleDelta.y / 120, "vertical");
                }

                ToolTip.visible: containsMouse && (modelData.tooltip?.title !== undefined && modelData.tooltip?.title !== "")
                ToolTip.text: modelData.tooltip?.title ?? ""
            }
        }
    }
}

import QtQuick
import QtQuick.Layouts
import Quickshell

PanelWindow {
    id: bar
    anchors {
        top: true
        left: true
        right: true
    }
    implicitHeight: 30
    color: "transparent"

    Rectangle {
        anchors.fill: parent
        color: "#222222"
        bottomLeftRadius: 20
        bottomRightRadius: 20
        // left
        RowLayout {
            anchors {
                left: parent.left
                leftMargin: 25
            }
            Loader { active: true; sourceComponent: Workspaces {} }
        }
        // center
        RowLayout {
            anchors {
                horizontalCenter: parent.horizontalCenter
                verticalCenter: parent.verticalCenter
            }

            Text {
                text: niri.focusedWindowTitle
                font.family: "Barlow Medium"
                font.pixelSize: 16
                color: "#999999"
            }
        }
        // right
        RowLayout {
            anchors {
                verticalCenter: parent.verticalCenter
                right: parent.right
                rightMargin: 25
            }
            spacing: 20
            Loader { active: true; sourceComponent: Systray { window: bar } }
            Loader { active: true; sourceComponent: Volume {} }
            Loader { active: true; sourceComponent: Power {} }
            Loader { active: true; sourceComponent: Time {} }
        }
    }
}

